import React from 'react';

/**
 * Defines the properties for the Badge component, ensuring all data from the spec is included.
 */
interface BadgeProps {
  /** The text content displayed inside the badge. Defaults to 'New'. */
  text?: string;
  /** The visual variant of the badge (e.g., 'error'). */
  variant?: 'error' | 'success' | 'warning' | 'info' | 'default' | string;
  /** Alias for text content, common in component data. */
  children?: string;
}

/**
 * A small, typically rounded component used to highlight an item's status or count.
 * This component is styled to exactly match the provided JSON specifications.
 */
const Badge: React.FC<BadgeProps> = ({
  text = 'New',
  children,
  variant = 'error',
}) => {
  // Use children if provided, otherwise fall back to text, then default 'New'
  const content = children ?? text;

  // --- Figma Styling Mapping based on componentData.styles ---
  // backgroundColor: rgba(239, 68, 68, 1) -> bg-red-500 (Tailwind equivalent for #EF4444)
  // color: rgba(255, 255, 255, 1) -> text-white
  // padding: 4px 8px -> py-1 px-2 (4px = 0.25rem; 8px = 0.5rem)
  // borderRadius: 9999px -> rounded-full
  // fontSize: 12px -> text-xs
  // fontWeight: 600 -> font-semibold
  // width: 60px -> w-[60px]
  // height: 24px -> h-[24px]

  const baseClasses = `
    inline-flex items-center justify-center
    w-[60px] h-[24px]
    bg-red-500 text-white
    text-xs font-semibold
    py-1 px-2
    rounded-full
    whitespace-nowrap
    leading-none
  `;

  return (
    <span
      className={baseClasses}
      role="status"
      aria-label={`${variant} badge: ${content}`}
    >
      {content}
    </span>
  );
};

export default Badge;

