import React from 'react';

/**
 * Interface for the Button component props.
 * The component must strictly adhere to the provided JSON data for styling and content.
 */
interface ButtonProps {
  /** The text content displayed inside the button. Defaults to 'Button'. */
  text?: string;
  /** Optional click handler for the button. */
  onClick?: (event: React.MouseEvent<HTMLButtonElement>) => void;
  /** Accessibility label for the button. */
  ariaLabel?: string;
}

/**
 * The Button component, styled according to the provided specifications:
 * - backgroundColor: rgba(124, 58, 237, 1) -> #7C3AED -> bg-violet-600
 * - color: rgba(250, 250, 250, 1) -> #FAFAFA -> text-gray-50
 * - padding: 8px 16px -> py-2 px-4
 * - borderRadius: 6px -> rounded-md
 * - fontSize: 14px -> text-sm
 * - fontWeight: 500 -> font-medium
 * - width: 80px -> w-[80px]
 * - height: 36px -> h-[36px]
 * - text: "Button"
 */
const Button: React.FC<ButtonProps> = ({
  text = 'Button',
  onClick,
  ariaLabel = 'Action button',
}) => {
  // We use standard Tailwind classes where possible, and arbitrary values for exact dimensions.
  // 8px padding vertical matches py-2 (0.5rem)
  // 16px padding horizontal matches px-4 (1rem)
  // 6px border radius matches rounded-md
  // 14px font size matches text-sm
  // 500 font weight matches font-medium

  const baseClasses = `
    flex items-center justify-center
    w-[80px] h-[36px]
    bg-violet-600 text-gray-50
    py-2 px-4
    rounded-md
    text-sm font-medium
    transition duration-150 ease-in-out
    focus:outline-none focus:ring-2 focus:ring-violet-500 focus:ring-offset-2
    hover:bg-violet-700
    whitespace-nowrap
    cursor-pointer
    border border-transparent
  `;

  return (
    <button
      className={baseClasses}
      onClick={onClick}
      aria-label={ariaLabel}
      type="button"
    >
      {text}
    </button>
  );
};

export default Button;

