import React from 'react';

/**
 * Interface for the Input component props.
 * Note: The component data implies the input itself has a fixed width and height.
 */
interface InputProps {
  /** The type of the input element (e.g., 'text', 'password'). */
  type?: 'text' | 'password' | 'email' | 'number' | 'search';
  /** The placeholder text displayed in the input field. */
  placeholder?: string;
  /** The current value of the input field. */
  value?: string;
  /** The label associated with the input field (optional, since the design doesn't show one). */
  label?: string;
  /** Accessibility label for the input field. */
  ariaLabel?: string;
  /** Optional handler for input changes. */
  onChange?: (event: React.ChangeEvent<HTMLInputElement>) => void;
}

/**
 * A pixel-perfect input component matching the provided Figma specifications.
 * Styles derived from componentData:
 * - Background: rgba(255, 255, 255, 1) -> bg-white
 * - Border: 1px solid rgba(209, 213, 219, 1) -> border border-gray-300
 * - Border Radius: 6px -> rounded-md
 * - Padding: 8px 12px -> py-2 px-3
 * - Font Size: 14px -> text-sm
 * - Color (Placeholder/Text): rgba(156, 163, 175, 1) -> text-gray-400 (used for placeholder color)
 * - Width: 240px -> w-[240px]
 * - Height: 40px -> h-[40px]
 */
const Input: React.FC<InputProps> = ({
  type = 'text',
  placeholder = 'Enter text...',
  value = 'Enter text...',
  label = '', // Not rendered, but included for completeness
  ariaLabel,
  onChange,
}) => {
  const inputId = React.useId();

  // Determine the final value for aria-label
  const finalAriaLabel = ariaLabel || label || placeholder || 'Text input field';

  return (
    <div className="flex flex-col">
      {/* Label is included for semantic completeness, though hidden if empty */}
      {label && (
        <label htmlFor={inputId} className="sr-only">
          {label}
        </label>
      )}
      <input
        id={inputId}
        type={type}
        // Fixed dimensions and styling based on specifications
        className="
          w-[240px] h-[40px] 
          bg-white 
          border border-[#D1D5DB] 
          rounded-[6px] 
          py-2 px-3 
          text-sm 
          text-[#9CA3AF] 
          placeholder-[#9CA3AF] 
          focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500
        "
        placeholder={placeholder}
        value={value}
        onChange={onChange}
        aria-label={finalAriaLabel}
      />
    </div>
  );
};

export default Input;

